Write-Host 'Use this to login:' -ForegroundColor Green
Write-Host 'docker login docker.pkg.github.com --username {name} -p {tokesecret}' -ForegroundColor Yellow
Write-Host 'get {tokesecret} by https://help.github.com/en/articles/creating-a-personal-access-token-for-the-command-line' -ForegroundColor Blue